let inputs = runtime.inputs();
let outputs = runtime.outputs();
let awsData = inputs.aws_data;
let gcvData = JSON.parse(inputs.gcv_data);
let features = new Set();
let colors = new Set();

function numberToHex(num) {
    let hex = num.toString(16).toUpperCase();

    hex = hex.length < 2 ? '0'+ hex : hex;

    return hex;
}

if (!!gcvData && gcvData.length > 0) {
    awsData.forEach(feature => features.add(feature.label));
}

if (!!gcvData && !!gcvData.responses && gcvData.responses.length > 0) {
    let annotations = gcvData.responses[0];
    
    if (!!annotations.labelAnnotations && annotations.labelAnnotations.length > 0) {
        annotations.labelAnnotations
            .forEach(annotation => features.add(annotation.description.trim()));
    }

    if (!!annotations.textAnnotations
        && annotations.textAnnotations.length > 0) {
        annotations.textAnnotations
            .forEach(annotation => features.add(annotation.description.trim()));
    }

    if (!!annotations.imagePropertiesAnnotation 
        && !!annotations.imagePropertiesAnnotation.dominantColors
        && !!annotations.imagePropertiesAnnotation.dominantColors.colors
        && annotations.imagePropertiesAnnotation.dominantColors.colors.length > 0)
    annotations.imagePropertiesAnnotation
        .dominantColors
        .colors
        .forEach(colorInfo => {
            let red = colorInfo.color.red;
            let green = colorInfo.color.green;
            let blue = colorInfo.color.blue;

            let hex = numberToHex(red) + numberToHex(green) + numberToHex(blue);
            let color = (!!lucy.hexToColor ? lucy.hexToColor(hex) : "") || "";

            colors.add({hex, color});
        });
}

outputs.features = {
    labels: Array.from(features),
    colors: Array.from(colors),
};

runtime.done(outputs);

let inputs = runtime.inputs();
let description = xstr(inputs.Description).trim();
let collection = lucy.currentModel().collections();
let featuresRegexFilter = description.trim()
    .split(' ')
    .map(desc => {
        return {
            'Features': { 
                '$regex': '.*' + desc.trim() + '*',
                '$options': 'i'
            }
        };
    });
let filter = {
    '$or': [
        {
            'Description': {
                '$regex': '.*' + description.replace(' ', '\ ') +'.*',
                '$options': 'i'
            }
        }
    ].concat(featuresRegexFilter)
};

if (description === '') {
    filter = {};
}

collection.findMany('Items', filter, {sort: {_id: -1}})
    .then(result => {
        runtime.done({result});
    })
    .catch(err => runtime.error(err));

