let inputs = runtime.inputs();
let features = inputs.features;
let collections = lucy.currentModel().collections();
let match = {id:null,name:null,email:null};

try {
    features = JSON.parse(features);
} catch {}

function intersect(lostItems, features) {
    let totalFeatures = features.size;   

    for(let lostItem of lostItems)  {
        // clean the data
        let lostFeatures = new Set(
            lostItem.Features.map(feature => feature.trim().toLowerCase())
        );

        let intersection = new Set(
            [...features].filter(feature => lostFeatures.has(feature))
        );

        if (intersection.size >= totalFeatures / 2) {
            match = {
                id: lostItem['_id'],
                name: lostItem['Name'],
                email: lostItem['Email']
            }

            break;
        }
    }

    runtime.done({name:match.name,email:match.email,id:match.id});
}

if (features.length > 0) {
    // clean the data
    features = new Set(
        features.map(feature => feature.trim().toLowerCase())
    );

    // get all lost items
    collections.findMany('Items', {}, {})
        .then(lostItems => intersect(lostItems, features))
        .catch(err => runtime.error(err));
} else {
    runtime.done({name:match.name,email:match.email,id:match.id});
}

